import "./Expression.css";
const Expression = () => {
  const title = "Expression";
  const arry = ["item1", "item2", "item3"];
  const hello = (arg) => `${arg}Function`;
  const jsx = <h3>Hello JSssssssX</h3>;
  return (
    <div className="expression">
      <h3>Hello {title}</h3>
      <h3>{arry}</h3>
      <h3>{hello("hello")}</h3>
      <h3>Hello JSX</h3>
      {jsx}
    </div>
  );
};

export default Expression;
